        //             offsetY: 0,
        //          },
        //          plotOptions: {
        //             radialBar: {
        //                hollow: {
        //                   size: "20%",
        //                },
        //             },
        //          },
        //       },
        //    },
        // ],

        fill: {
          opacity: 1,
        },

        colors: ["#6418C3", "#9859E7", "#E1CAFF"],
        labels: ["New", "Recover", "In Treatment"],
        legend: {
          fontSize: "16px",
          show: false,
        },
      },
    };
  }

  render() {
    return (
      <div id="chart">
        <ReactApexChart
          options={this.state.options}
          series={this.state.series}
          type="radialBar"
          height={330}
        />
      </div>
    );
  }
}
export default ChartCircle;
